var title_f40_8_41100="Neutropenic children infections";
var content_f40_8_41100=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F65091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F65091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk and types of infections in pediatric hematology patients with neutropenia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Predominant causes of fever",
"       </td>",
"       <td class=\"subtitle1\">",
"        Initial management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low risk",
"       </td>",
"       <td>",
"        Otherwise healthy child with transient, isolated neutropenia",
"       </td>",
"       <td>",
"        Viral infection",
"       </td>",
"       <td>",
"        Appropriate treatment for age or site of localized infection; careful follow-up",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Low-moderate risk",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         Chronic benign neutropenia",
"        </p>",
"        <p>",
"         (chronic autoimmune and idiopathic neutropenia)",
"        </p>",
"       </td>",
"       <td>",
"        Common &mdash; Upper respiratory tract infections, acute otitis media, skin infections, gingivitis, mouth ulcers, labial cellulitis or abscess in girls",
"       </td>",
"       <td>",
"        Oral antibiotics and close outpatient management for patients with adequate marrow reserves",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less common &mdash; Pneumonia, periorbital cellulitis, meningitis, sepsis",
"       </td>",
"       <td>",
"        Hospitalization and parenteral antibiotics for complicated febrile illnesses (eg, hemodynamic instability, skin or soft-tissue infection, pneumonia, concern for catheter-related infection); hospitalization also may be warranted in patients with prior complicated febrile illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Cyclic neutropenia",
"       </td>",
"       <td>",
"        Frequent &mdash; Oral ulcers, gingivitis, periodontitis, pharyngitis, adenopathy",
"       </td>",
"       <td>",
"        Generally do not require antibiotics for fevers or mucocutaneous inflammatory lesions that are typical during neutrophil nadirs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less frequent &mdash; Bacteremia, cellulitis, acute otitis media, sinusitis, pneumonia, appendicitis",
"       </td>",
"       <td>",
"        Careful assessment of the need for hospitalization as well as administration of antibiotics for atypical fevers or lesions, or infections such as acute otitis media, sinusitis, and lower respiratory tract disease; hospitalization also may be warranted in patients with prior complicated febrile illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uncommon &mdash;",
"        <em>",
"         Clostridium",
"        </em>",
"        -associated necrotizing enterocolitis",
"       </td>",
"       <td>",
"        Hospitalization and broad-spectrum parenteral antibiotics for complaints of abdominal pain, significant localized infection, or signs of sepsis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        High risk",
"       </td>",
"       <td>",
"        Severe congenital neutropenia (Kostmann syndrome)",
"       </td>",
"       <td>",
"        Omphalitis, cellulitis, perirectal abscess, sepsis",
"       </td>",
"       <td>",
"        Hospitalization and initiation of broad-spectrum antibiotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aplastic anemia",
"       </td>",
"       <td>",
"        Respiratory tract infections, soft tissue infections, bacteremia, invasive fungal disease, especially due to",
"        <em>",
"         Aspergillus",
"        </em>",
"        spp.",
"       </td>",
"       <td>",
"        Hospitalization and initiation of broad-spectrum antibiotics; antifungal agents may be warranted",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ill-appearing child with neutropenia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Hospitalization and initiation of broad-spectrum antibiotics",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
