"   <div class=\"list ubnlist drugH1Div drugBrandNames\" id=\"F220473\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Brand Names: U.S.",
"    </span>",
"    <ul>",
"     <li>",
"      Black Draught&reg; [OTC];",
"     </li>",
"     <li>",
"      Evac-U-Gen&reg; [OTC];",
"     </li>",
"     <li>",
"      ex-lax&reg; Maximum Strength [OTC];",
"     </li>",
"     <li>",
"      ex-lax&reg; [OTC];",
"     </li>",
"     <li>",
"      Fleet&reg; Pedia-Lax&trade; Quick Dissolve [OTC];",
"     </li>",
"     <li>",
"      Fletcher's&reg; [OTC];",
"     </li>",
"     <li>",
"      Geri-kot [OTC];",
"     </li>",
"     <li>",
"      Little Tummys&reg; Laxative [OTC];",
"     </li>",
"     <li>",
"      Perdiem&reg; Overnight Relief [OTC];",
"     </li>",
"     <li>",
"      Senexon&reg; [OTC];",
"     </li>",
