" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Borderline personality disorder: Treatment and prognosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/1/42009/contributors\">",
