" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Genetic factors in the amyloid diseases",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/10/42153/contributors\">",
