var title_f41_11_42171="Criteria for initiation of antibiotics for UTI";
var content_f41_11_42171=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F61419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F61419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Minimum criteria for the initiation of antibiotics for urinary tract infection in long-term care residents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        With indwelling catheter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        At least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fever (&gt;37.9&deg;C [100&deg;F] or 1.5&deg;C [2.4&deg;F] above baseline)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New CVA tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rigors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        New onset delirium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Without indwelling catheter:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute dysuria",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fever (&gt;37.9&deg;C [100&deg;F] or 1.5&deg;C [2.4&deg;F] above baseline)",
"        <strong>",
"         AND",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        At least one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        New or worsening urgency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Suprapubic pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Gross hematuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        CVA tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Urinary incontinence",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Source: Loeb M, Bentley DW, Bradley S, et al. Development of minimum criteria for the initiation of antibiotics in residents of long-term-care facilities: results of a consensus conference. Infect Control Hosp Epidemiol 2001; 22:120.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
