" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Brief intervention for unhealthy alcohol and other drug use",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Richard Saitz, MD, MPH, FACP, FASAM",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Andrew J Saxon, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?41/17/42265/contributors\">",
"     Richard Hermann, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
