" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Therapeutic use of warfarin",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/17/42266/contributors\">",
