var title_f41_17_42268="Imaging ACS";
var content_f41_17_42268=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F74994&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F74994&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Strengths and weaknesses of available noninvasive imaging modalities for patients presenting with suspected ACS in an ED setting",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Modality",
"       </td>",
"       <td class=\"subtitle1\">",
"        Strengths",
"       </td>",
"       <td class=\"subtitle1\">",
"        Weaknesses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myocardial perfusion scintigraphy",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Widely available",
"        </p>",
"        <p>",
"         &bull; Standard interpretive approaches, can be quantitated",
"        </p>",
"        <p>",
"         &bull; Images the process responsible for symptoms",
"        </p>",
"        <p>",
"         &bull; Multiple RCTs and observational data support use",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Usually not available 24/7",
"        </p>",
"        <p>",
"         &bull; Rest imaging not as useful &gt;&sim;2 to 3 hours after symptoms but stress imaging is useful",
"        </p>",
"        <p>",
"         &bull; Entails radiation exposure",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiography",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Widely available",
"        </p>",
"        <p>",
"         &bull; Standard interpretive approaches",
"        </p>",
"        <p>",
"         &bull; Images a process associated with symptoms",
"        </p>",
"        <p>",
"         &bull; RCT and observational data support use",
"        </p>",
"        <p>",
"         &bull; Relatively low cost",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; May not be available 24/7",
"        </p>",
"        <p>",
"         &bull; Rest imaging not as useful after symptom cessation but stress imaging is useful",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coronary CTA",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Widely available",
"        </p>",
"        <p>",
"         &bull; Standard qualitative interpretive approaches",
"        </p>",
"        <p>",
"         &bull; Images anatomy indirectly associated with symptoms",
"        </p>",
"        <p>",
"         &bull; Multiple RCTs and observational data support use",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; May not be available 24/7",
"        </p>",
"        <p>",
"         &bull; Coronary calcium may interfere with interpretation",
"        </p>",
"        <p>",
"         &bull; Implications of \"moderate\" stenoses or non-obstructive plaque not clear, patients would need further testing",
"        </p>",
"        <p>",
"         &bull; Substantial minority of patients not eligible due to renal dysfunction or atrial fibrillation",
"        </p>",
"        <p>",
"         &bull; Entails radiation exposure",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiography with contrast for perfusion imaging",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Images the process responsible for symptoms",
"        </p>",
"        <p>",
"         &bull; Large observational data suggests incremental diagnostic information",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Not available for general use - contrast agents for perfusion not FDA approved",
"        </p>",
"        <p>",
"         &bull; Interpretive approaches not standardized",
"        </p>",
"        <p>",
"         &bull; Most data come from specialized centers",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiovascular MR",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Comprehensively images anatomy and physiology associated with symptoms (including coronary arteries, myocardial perfusion, infarction, ventricular function)",
"        </p>",
"        <p>",
"         &bull; Small amount of observational data support use",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Expertise for comprehensive imaging not widely available",
"        </p>",
"        <p>",
"         &bull; Interpretive approaches not standardized",
"        </p>",
"        <p>",
"         &bull; Data come from specialized centers",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CTA: computed tomographic angiography; MR: magnetic resonance; RCT: randomized controlled trial.",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Drs. Prem Soman and James Udelson.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
