var title_f41_18_42284="Diag considerations nocturia";
var content_f41_18_42284=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F82467&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F82467&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of important diagnostic considerations in the evaluation of the patient with nocturia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Element",
"      </td>",
"      <td class=\"subtitle1\">",
"       Specific aspect",
"      </td>",
"      <td class=\"subtitle1\">",
"       Rationale",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       History",
"      </td>",
"      <td>",
"       Review of medical history",
"      </td>",
"      <td>",
"       Heart failure, diabetes mellitus, hypertension, obstructive sleep apnea, and peripheral edema particularly relevant; Narrow angle glaucoma is a contraindication for bladder relaxant therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Fluid intake",
"      </td>",
"      <td>",
"       Evaluation for excessive fluid intake (psychogenic polydypsia, health belief)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Medications, especially for diuretics",
"      </td>",
"      <td>",
"       Late afternoon or evening diuretic use may cause nocturia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sleep and related conditions",
"      </td>",
"      <td>",
"       Information about nighttime pain, depression, or insomnia or difficulty with sleep maintenance is important",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dizziness, low blood pressure or orthostasis, or history of accidental falls",
"      </td>",
"      <td>",
"       May be a contraindication for non-selective alpha-blocker therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dementia or mild cognitive impairment",
"      </td>",
"      <td>",
"       May be contraindication, use antcholinergic drugs with caution",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"5\">",
"       Physical examination",
"      </td>",
"      <td>",
"       Supine and orthostatic blood pressure",
"      </td>",
"      <td>",
"       Particularly if alpha-blocker therapy (for men) is considered",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cardiovascular and pulmonary examination",
"      </td>",
"      <td>",
"       Examination for fluid overload or heart failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Abdominal examination",
"      </td>",
"      <td>",
"       Evaluation for suprapubic distention and tenderness (insensitive, but highly specific if found)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rectal examination",
"      </td>",
"      <td>",
"       Evaluation for prostate size, rectal masses, or fecal impaction, analysis of resting and volitional contraction (useful for employing behavioral therapy, including urge suppression strategies)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Neurological examination",
"      </td>",
"      <td>",
"       Neurological conditions (spinal cord injury or multiple sclerosis)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Laboratory studies",
"      </td>",
"      <td>",
"       Urinalysis",
"      </td>",
"      <td>",
"       Urinary tract pathology, hematuria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Electrolyte panel",
"      </td>",
"      <td>",
"       Evaluation for abnormal renal function; check for glycemic control in patients with diabetes mellitus; examination for low serum sodium (especially for consideration of ddAVP therapy or monitoring)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Frequency volume chart",
"      </td>",
"      <td>",
"       Nocturnal polyuria, functional bladder capacity, total 24 hour urine output",
"      </td>",
"      <td>",
"       More accurate description of patient nocturnal urinary patterns",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Additional studies",
"      </td>",
"      <td>",
"       Noninvasive uroflowometry (in men)",
"      </td>",
"      <td>",
"       Low urine flow rate (4 to 15 mL/sec) more suggestive of BPH; very low flow rate (&lt;4 mL/sec) may indicate need for surgical treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       PVR by ultrasound",
"      </td>",
"      <td>",
"       Post void residual over 200 mL may be causative of nocturia or may prevent use of a bladder relaxant",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
