var title_f41_2_42028="Drug eruptions clin manifestations and chronology";
var content_f41_2_42028=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F86515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F86515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical manifestations and chronology of cutaneous drug reactions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical manifestation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chronology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Morphology and localization",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Urticaria",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Immediate",
"        </p>",
"        <p>",
"         Minutes to two hours",
"        </p>",
"        <p>",
"         Rapid change and resolution of single lesions (&lt;24 hours)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Urticarial wheals, pale swelling surrounded by erythematous border",
"        </p>",
"        <p>",
"         Pruritus",
"        </p>",
"        <p>",
"         Trunk, face, mucosae, or generalized",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Angioedema",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Immediate to delayed",
"        </p>",
"        <p>",
"         Minutes up to six hours",
"        </p>",
"        <p>",
"         Resolution 24 to 72 hours",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Pale swelling",
"        </p>",
"        <p>",
"         Dysesthesia, pain",
"        </p>",
"        <p>",
"         Face, lips, eye lids, mucosa, male genitals",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Maculopapular exanthem",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed",
"        </p>",
"        <p>",
"         Typically 6 to 24 hours",
"        </p>",
"        <p>",
"         Peak at 48 to 72 hours",
"        </p>",
"        <p>",
"         Resolution one week",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Macules, papules",
"        </p>",
"        <p>",
"         Confluence, desquamation",
"        </p>",
"        <p>",
"         Trunk, proximal extremities, or disseminated",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Fixed drug eruption",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed",
"        </p>",
"        <p>",
"         Typically 30 minutes to 8 hours",
"        </p>",
"        <p>",
"         Resolution 7 to 10 days",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Erythematous or lilac macule or plaque, central bulla/erosion, permanent hyperpigmentation, burning, pruritus",
"        </p>",
"        <p>",
"         Fixed (identical)",
"        </p>",
"        <p>",
"         Acral or mucosal localization",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Pustular exanthem (AGEP)",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed",
"        </p>",
"        <p>",
"         Typically 6 to 12 hours up to 48 hours",
"        </p>",
"        <p>",
"         Resolution one week",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Confluent erythema, pinpoint nonfollicular pustules, pruritus, massive desquamation",
"        </p>",
"        <p>",
"         Folds, trunk, or generalized",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Vesicobullous exanthems",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed",
"        </p>",
"        <p>",
"         Typically 24 to 48 hours",
"        </p>",
"        <p>",
"         Resolution several weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Macules, bullae, epidermolysis, mucositis",
"        </p>",
"        <p>",
"         Initially painful skin",
"        </p>",
"        <p>",
"         Trunk, face, or generalized",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Original table modified for this publication. Bircher AJ, Scherer K. Delayed cutaneous manifestations of drug hypersensitivity. Med Clin North Am 2010; 94:711. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
