var title_f41_20_42315="Chemical peels";
var content_f41_20_42315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F75958&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F75958&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Types of chemical peels",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Depth",
"       </td>",
"       <td class=\"subtitle1\">",
"        Agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Very superficial",
"       </td>",
"       <td>",
"        Glycolic acid, 30-50 percent applied for 1-2 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jessner (resorcinol, salicylic acid, lactic acid, ethanol) solution applied in 1-3 coats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low concentration resorcinol, 20-30 percent applied for 5-10 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TCA (trichloroacetic acid) 10 percent applied in 1 coat",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Superficial",
"       </td>",
"       <td>",
"        Glycolic acid, 50-70 percent, applied for 2-5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pyruvic acid, 40-50 percent applied for 3-5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jessner solution applied in 4-10 coats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Resorcinol, 40-50 percent applied for 30-60 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TCA, 10-30 percent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Medium",
"       </td>",
"       <td>",
"        Glycolic acid 70 percent applied for 3-15 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pyruvic acid 60 percent applied for 3-5 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TCA, 35-50 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Augmented TCA (carbon dioxide and TCA 35 percent; Jessner solution and TCA 35 percent; glycolic acid 70 percent and TCA 35 percent)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Deep",
"       </td>",
"       <td>",
"        Phenol 88 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Baker-Gordon phenol formula (88 percent phenol, distilled water, septisol, croton oil)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Source: Fabbrocini G, De Padova MP, Tosti A. Chemical peels: what's new and what isn't new but still works well. Facial Plast Surg 2009; 25:329.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
