var title_f41_22_42347="Diagnosis of Scedosporium";
var content_f41_22_42347=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F52154&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F52154&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic tools for Scedosporium infections",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Diagnostic tool",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Radiographic appearance",
"       </td>",
"       <td>",
"        Lung: variable; alveolar or reticulonodular infiltrates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brain: hypodense lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Microscopy",
"       </td>",
"       <td>",
"        Potassium hydroxide &plusmn; calcoflour white stains: septate, branching hyphae at 45&deg; angles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        Culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" rowspan=\"2\">",
"        Macroscopic appearance",
"       </td>",
"       <td class=\"sublist_other\">",
"        S. apiospermum: rapid growing, cottony, white or gray, then gray or brown with age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        S.  prolificans: white, cottony, with a greenish gray to dark green reverse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" rowspan=\"2\">",
"        Microscopic appearance",
"       </td>",
"       <td class=\"sublist_other\">",
"        S. apiospermum: cylindrical, single, uninucleate, non-pigmented conidiogenous cells; cleistothecia may be produced",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        S. prolificans: flask-shaped, basally swollen or inflated, clustered, non-pigmented conidiogenous cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Histopathology",
"       </td>",
"       <td>",
"        Septate hyphae branching at 45&deg; angle in inflammation, including granulomata and necrosis; adventitious sporulation may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Molecular methods",
"       </td>",
"       <td>",
"        Investigational; PCR and sequencing have been used for laboratory identification and outbreak investigations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serology",
"       </td>",
"       <td>",
"        Antibody detection not reliable; (1 &rarr; 3) &beta;-D glucan antigen testing may be helpful, but needs further validation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
