"    <li class=\"bulletItem\">",
"     <a class=\"graphic graphic_table\" href=\"mobipreview.htm?23/39/24183\" title=\"Lexicomp clinical abbreviations\">",
"      Lexicomp clinical abbreviations",
"     </a>",
"    </li>",
"   </ul>",
"  </div>",
"  <h1>",
"   RELATED TOPICS",
"  </h1>",
"  <div id=\"relatedTopics\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"drug drug_patient\" href=\"mobipreview.htm?8/32/8708?source=related_link\">",
"      Quinagolide: Patient drug information",
"     </a>",
"    </li>",
