"  <div id=\"mc3k\">",
"   <form action=\"\" id=\"BodyMassIndexPatient_form\" name=\"BodyMassIndexPatient_form\" onkeydown=\"clrResults();\" onkeyup=\"BodyMassIndexPatient_fx();\" onreset=\"rrclr();\" onsubmit=\"return false;\">",
"    <table cellpadding=\"4\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\" width=\"100%\">",
