var script_f41_23_42366=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function BodyMassIndexPatient_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.BodyMassIndexPatient_form){",
"",
"",
"doCalc = true;",
"inches = parseFloat(Height_inch_param.value);",
"if (isNaN(inches)) inches = 0;",
"param_value = (parseFloat(Height_foot_param.value) * 12) + inches;",
"Height = param_value;",
"if (Weight_param.value.indexOf(',') >= 0){ Weight_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Weight_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"Weight = param_value;",
"dp = 1;",
"BMI =  (Weight/2.205) / power((Height/39.37), 2);",
"",
"if (doCalc) BMI_param.value = fixDP(BMI, dp);",
"",
"if (isNaN(BMI_param.value)) BMI_param.value = '';",
"",
"",
"if (doCalc){",
"",
"rrclr();",
"if (BMI < 18.5){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((BMI >= 18.5) && (BMI < 25)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if ((BMI >= 25) && (BMI < 30)){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"if (BMI >= 30){ document.getElementById('rr1_4').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.BodyMassIndexPatient_form){",
"",
"if (Height_foot_param.value && Height_foot_param.value < 3) {",
"Height = 0;",
"Height_foot_param.value = \"\";",
"clrResults();",
"alert(\"Minimum input is 3 feet.\");",
"}",
"",
"if (Height_inch_param.value && Height_inch_param.value < 0) {",
"Height = 0;",
"Height_inch_param.value = \"\";",
"clrResults();",
"alert(\"Minimum input is 0 inches.\");",
"}",
"",
"if (Height_foot_param.value && Height_foot_param.value > 7) {",
"Height_foot_param.value = \"\";",
"clrResults();",
"Height = 0;",
"alert(\"Maximum input is 7 feet.\");",
"}",
"",
"if (Height_inch_param.value && Height_inch_param.value > 11) {",
"Height_inch_param.value = \"\";",
"clrResults();",
"Height = 0;",
"alert(\"Maximum input is 11 inches.\");",
"}",
"",
"if (Weight_param.value && Weight < 20) {",
"Weight = 0;",
"Weight_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Weight is 20 pounds.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Weight_param.value && Weight > 800) {",
"Weight_param.value = \"\";",
"clrResults();",
"Weight = 0;",
"doCalc = false;",
"alert(\"The maximum value for Weight is 800 pounds.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.BodyMassIndexPatient_form){",
"",
"BMI_param.value = '';",
"",
"rrclr();",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
