var title_f41_24_42380="Lab findings thalassemias";
var content_f41_24_42380=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F50393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F50393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The thalassemias: Genetic, clinical, and laboratory findings",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Genotype",
"       </td>",
"       <td class=\"subtitle1\">",
"        MCV",
"       </td>",
"       <td class=\"subtitle1\">",
"        Anemia",
"       </td>",
"       <td class=\"subtitle1\">",
"        Hb electrophoresis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Alpha thalassemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Silent carrier",
"       </td>",
"       <td>",
"        &alpha; &alpha; / &alpha; -",
"       </td>",
"       <td>",
"        NL",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        <p>",
"         Normal",
"        </p>",
"        <p>",
"         &lt;3 percent Hb Barts at birth",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trait",
"       </td>",
"       <td>",
"        <p>",
"         &alpha; &alpha; / - - or",
"        </p>",
"        <p>",
"         &alpha; - / &alpha; -",
"        </p>",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Mild",
"       </td>",
"       <td>",
"        <p>",
"         Normal",
"        </p>",
"        <p>",
"         3 to 8 percent Hb Barts at birth",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hb H disease (deletional)",
"       </td>",
"       <td>",
"        &alpha; - / - -",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Moderate",
"       </td>",
"       <td>",
"        <p>",
"         5 to 30 percent Hb H present in adults",
"        </p>",
"        <p>",
"         20 to 40 percent Hb Barts at birth",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Major (fetal hydrops)",
"       </td>",
"       <td>",
"        - - / - -",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Fatal",
"       </td>",
"       <td>",
"        <p>",
"         Hb Barts, Hb Portland, and Hb H present",
"        </p>",
"        <p>",
"         Hb A, Hb F, and Hb A2 are absent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Beta thalassemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trait",
"       </td>",
"       <td>",
"        <sub>",
"         &beta;",
"        </sub>",
"        /",
"        <sub>",
"         &beta;",
"        </sub>",
"        &deg;",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Mild",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         Hb A2 increased (3.5 to 7 percent)",
"        </p>",
"        <p>",
"         Hb F increased in about 50 percent of patients",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intermedia",
"       </td>",
"       <td>",
"        <sub>",
"         &beta;",
"        </sub>",
"        <sup>",
"         +",
"        </sup>",
"        /",
"        <sub>",
"         &beta;",
"        </sub>",
"        <sup>",
"         +",
"        </sup>",
"        and others*",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Moderate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Major",
"       </td>",
"       <td>",
"        <sub>",
"         &beta;",
"        </sub>",
"        &deg; /",
"        <sub>",
"         &beta;",
"        </sub>",
"        &deg;",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Severe",
"       </td>",
"       <td>",
"        <p>",
"         Hb A absent",
"        </p>",
"        <p>",
"         Only Hb A2 and Hb F are present",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MCV: mean corpuscular volume; Hb: hemoglobin; NL: normal;",
"     <sub>",
"      &beta;",
"     </sub>",
"     <sup>",
"      +",
"     </sup>",
"     : thalassemic gene producing some",
"     <sub>",
"      &beta;",
"     </sub>",
"     -chain;",
"     <sub>",
"      &beta;",
"     </sub>",
"     &deg;: thalassemic gene producing no",
"     <sub>",
"      &beta;",
"     </sub>",
"     -chain.",
"     <br>",
"      * See text for multiple other genotypes.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Stephen A Landaw, MD, PhD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
