var title_f41_25_42395="ASCO recommend lung CA followup";
var content_f41_25_42395=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F52008&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F52008&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    2004 ASCO* recommendations for lung cancer follow-up care",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Guideline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        History and physical examination",
"       </td>",
"       <td>",
"        For",
"patients treated with curative intent, in the absence of symptoms, a",
"history and physical exam should be performed every 3 months during the",
"first 2 years; every 6 months thereafter through year 5, and yearly thereafter.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest radiographs",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        For patients treated with curative",
"intent, there is no clear role for routine chest x-rays in asymptomatic",
"patients and for those in whom no intervention is planned.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Complete blood cell count",
"       </td>",
"       <td colspan=\"1\" rowspan=\"7\">",
"        Only performed as indicated by the patient's symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        CT scans",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        Automated chemistry studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        Bone scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        Bronchoscopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        CT/MRI of the brain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"1\">",
"        PET scan",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * ASCO: American Society of Clinical Oncology.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Pfister, DG, et al. J Clin Oncol 2004; 22:330.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
