var title_f41_29_42461="Paramedic thrombolysis examination checklist";
var content_f41_29_42461=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63223&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63223&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Paramedic thrombolysis examination checklist",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"centered\" colspan=\"5\">",
"        <p>",
"         Wilkes County Emergency Medical Service",
"        </p>",
"        <p>",
"         Advanced level practical skills examination",
"        </p>",
"        <strong>",
"         Thrombolytic therapy",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td colspan=\"5\">",
"        Date:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        Candidate name:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        Examiner signature:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        Level:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- EMT-P",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Automatic failure = critical critera",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"4\">",
"        Cumulative failure = 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Exam series:&nbsp;&nbsp;&nbsp;- A&nbsp;&nbsp;&nbsp;- B&nbsp;&nbsp;&nbsp;- C&nbsp;&nbsp;&nbsp;- D",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Time start:",
"        <br/>",
"        &nbsp;",
"        <br/>",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Time finish:",
"        <br/>",
"        &nbsp;",
"        <br/>",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Yes",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Takes appropriate precautions for personal protection [may verbalize]",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Assures that all steps on chest pain/suspected cardiac event protocol have been completed",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Assures patient has two (2) patent IV lines or has twin catheter that is functional",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Correctly interprets 12-lead ECG and transmits [may verbalize] to ED for physician review",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Performs history and assessment of acute MI signs and symptoms",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Repeats 12-lead ECG and correctly interprets 12-lead ECG",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Performs neurological assessment for thrombolytics",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Completes thrombolytic therapy screening form",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Performs [may verbalize] rectal exam using hemoccult slide and developer",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Contacts medical control and provides patient information and requests permission for thrombolytic therapy",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Ensures that all needed adjunctive medications and therapies have been administered",
"        <em>",
"         (according to WCEMS protocols)",
"        </em>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Administers enoxaparin (Lovenox) with correct dosage and route according to age and weight",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Properly reconstitutes tenecteplase (TNKase) for administration",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Administers the correct weight-based dose of TNKase over 5 seconds",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Verbalizes need for frequent assessment of patient clinical status",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Verbalizes need to contact medical control for patient report and transmission of repeat 12-lead ECG to ED for review",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Verbalizes need to document&nbsp;patient information&nbsp;with 12-lead ECG tracings and screening form",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"        At the completion of test submit this form to the examination representative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        <strong>",
"         Critical criteria",
"        </strong>",
"        <em>",
"         (document rationale for critical criteria on reverse side of this form)",
"        </em>",
"        <ul>",
"         <li>",
"          Failure to assure two patent IV lines or&nbsp;one twin catheter",
"         </li>",
"         <li>",
"          Failure to perform a neurological assessment",
"         </li>",
"         <li>",
"          Failure to complete thrombolytic screening form",
"         </li>",
"         <li>",
"          Failure to&nbsp;provide all needed adjunctive medications and therapies",
"         </li>",
"         <li>",
"          Administers incorrect dose and or uses incorrect route of enoxaparin",
"         </li>",
"         <li>",
"          Administers incorrect weight based dose of TNKase",
"         </li>",
"         <li>",
"          Failure to verbalize need to reassess patient after enoxaparin/TNKase administration",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle1\" colspan=\"5\">",
"        <em>",
"         OFFICIAL USE ONLY - DO NOT WRITE IN THIS BOX",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Candidate result:",
"       </td>",
"       <td>",
"        Pass",
"       </td>",
"       <td>",
"        Fail",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"3\">",
"        Failure due to:",
"       </td>",
"       <td>",
"        Critical criteria",
"       </td>",
"       <td>",
"        Cumulative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"5\">",
"        Representative:",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle1_single\" colspan=\"5\">",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Henderson McGinnis, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
