" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute appendicitis in pregnancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?41/34/42536/contributors\">",
