var title_f41_48_42764="RECIST response criteria solid tumors";
var content_f41_48_42764=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F74693&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F74693&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Response Evaluation Criteria in Solid Tumors (RECIST)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Response assessment",
"       </td>",
"       <td class=\"subtitle1\">",
"        RECIST guideline, version 1.0",
"        <sup>",
"         [1]",
"        </sup>",
"       </td>",
"       <td class=\"subtitle1\">",
"        RECIST guideline, version 1.1",
"        <sup>",
"         [2]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Target lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CR",
"       </td>",
"       <td>",
"        Disappearance of all target lesions",
"       </td>",
"       <td>",
"        Disappearance of all target lesions and reduction in the short axis measurement of all pathologic lymph nodes to &le;10 mm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PR",
"       </td>",
"       <td>",
"        &ge;30 percent decrease in the sum of the longest diameter of the target lesions compared with baseline",
"       </td>",
"       <td>",
"        &ge;30 percent decrease in the sum of the longest diameter of the target lesions compared with baseline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PD",
"       </td>",
"       <td>",
"        <p>",
"         &ge;20 percent increase in the sum of the longest diameter of the target lesions compared to the smallest sum of the longest diameter recorded since treatment started",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         The appearance of one of more new lesions",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &ge;20 percent increase of at least 5 mm in the sum of the longest diameters of the target lesions compared with the smallest sum of the longest diameter recorded",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         The appearance of new lesions including those detected by FDG-PET",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        SD",
"       </td>",
"       <td>",
"        Neither PR nor PD",
"       </td>",
"       <td>",
"        Neither PR nor PD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Non-target lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CR",
"       </td>",
"       <td>",
"        Disappearance of all non-target lesions and normalization of tumor marker levels",
"       </td>",
"       <td>",
"        Disappearance of all non-target lesions and normalization of tumor marker levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IR, SD",
"       </td>",
"       <td>",
"        Persistence of one or more non-target lesions and/or the maintenance of tumor marker levels above normal limits",
"       </td>",
"       <td>",
"        Persistence of one or more non-target lesions and/or the maintenance of tumor marker levels above normal limits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PD",
"       </td>",
"       <td>",
"        Appearance of one or more new lesions and/or unequivocal progression of existing non-target lesions",
"       </td>",
"       <td>",
"        <p>",
"         The appearance of one of more new lesions or unequivocal progression",
"        </p>",
"        <p>",
"         If patient has measurable disease, an increase in the overall level, or substantial worsening in non-target lesions, such that tumor burden has increased, even if there is a SD or PR in target lesions",
"        </p>",
"        <p>",
"         If no measurable disease, an increase in the overall tumor burden comparable in magnitude to the increase that would be required to declare PD in measurable disease (eg, an increase in pleural effusions from trace to large, or an increase in lymphangitic disease from localized to widespread)",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CR: complete response; IR: incomplete response; PD: progressive disease; SD: stable disease.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Therasse P, et al. J Natl Cancer Inst 2000; 92:205.",
"       </li>",
"       <li>",
"        Eisenhauer E, et al. Eur J Cancer 2009; 45:228.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
