"     </li>",
"     <li>",
"      Diabeton Metilato (IT);",
"     </li>",
"     <li>",
"      Diabetose (JP);",
"     </li>",
"     <li>",
"      Diatol (HK, NZ);",
"     </li>",
"     <li>",
"      Dirastan (CZ);",
"     </li>",
"     <li>",
"      Dolipol (FR);",
"     </li>",
"     <li>",
"      Mellitos D (JP);",
"     </li>",
