var title_f41_49_42779="Risk assess crit breast ovar CA";
var content_f41_49_42779=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F65717&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F65717&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk assessment criteria for inherited breast-ovarian cancer syndome, combining several guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Non-Jewish families",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of breast cancer &le;40 yo in a FDR or SDR*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One FDR or SDR with breast and ovarian cancer, at any age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Two or more cases of breast cancer in FDRs or SDRs if one is diagnosed at &le;50 years old, or is bilateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One FDR or SDR with breast cancer at &le;50 years old, or bilateral and one FDR or SDR with ovarian cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Three cases of breast and ovarian cancer (at least one case of ovarian cancer) in FDRs and SDRs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Two cases of ovarian cancer in FDRs and SDRs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One case of male breast cancer in an FDR or SDR if another FDR or SDR has (male or female) breast or ovarian cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Jewish families",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Any of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One or more cases of breast cancer &le;50 years old in an FDR or SDR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One or more cases of ovarian cancer at any age in a FDR or SDR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One or more FDRs or SDRs with breast cancer at any age, if another FDR or SDR has breast and/or ovarian cancer at any age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        One or more cases of male breast cancer in an FDR or SDR",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * FDR: first-degree relative; SDR: second-degree relative.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Hampel H, et al. J Med Genet 2004; 41:81.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
