var script_f41_5_42067=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"function alertNaN(){",
"alert('This field is improperly formatted. You may only input the digits 0-9 and a decimal point.');  ",
"doCalc = false; ",
"clrResults();",
"}",
"",
"",
"function validNumberField(event) {",
"var field = event.target;",
"",
"if(field && field != 'undefined' && field.attributes.type.nodeValue && (field.attributes.type.nodeValue == 'number')) {",
"var val = field.value;",
"var charCode = event.charCode;",
"var key = String.fromCharCode(charCode);",
"",
"if(charCode == 0) {",
"return true;",
"}",
"",
"if(key == ' ') {",
"alert('Numeric field cannot contain spaces.');",
"return false;",
"}	",
"		",
"if(val.length == 0) {",
"if(key == '+') { return true; }",
"if(key == '-') { return true; }	",
"if(key == '.') { return true; }",
"}",
"		",
"if(key == '.') { return validFloat(val, key); }",
"",
"if(isNaN(parseInt(key))) {",
"alertNaN();",
"return false;",
"}",
"	",
"return validFloat(val, key);",
"}",
"return true;",
"}",
"",
"",
"function validFloat(val, key) {",
"var currentVal = parseFloat(val + key);",
"if(isNaN(val + key) || isNaN(currentVal)) {",
"alertNaN();",
"}",
"return true;",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function EstimatedDateDelivery_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 2000);",
"",
"with(document.EstimatedDateDelivery_form){",
"",
"",
"doCalc = true;",
"param_value = parseFloat(Gestational_Age_by_US_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Gestational_Age_by_US_unit.options[Gestational_Age_by_US_unit.selectedIndex].value.split('|');",
"Gestational_Age_by_US = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = 0;",
"monthname = new Array('Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec');",
"current_time = new Date;",
"lmp_time = new Date;",
"us_time = new Date;",
"edd_lmp = new Date;",
"edd_us = new Date;",
"var current_month_value = parseFloat(current_month.options[current_month.selectedIndex].value);",
"var current_date_value = parseFloat(current_date.options[current_date.selectedIndex].value);",
"var current_year_value = parseFloat(current_year.options[current_year.selectedIndex].value);",
"if ((current_month_value == 1) && (current_date_value > 28)){",
"if ((current_year_value % 4) == 0){ current_date_value = 29; current_date.selectedIndex = 28; }",
"else{ current_date_value = 28; current_date.selectedIndex = 27; };",
"}",
"if (((current_month_value == 3) || (current_month_value == 5) || (current_month_value == 8) || (current_month_value == 10)) && (current_date_value > 30)){ current_date_value = 30; current_date.selectedIndex = 29; };",
"current_time.setFullYear(current_year_value);",
"current_time.setMonth(current_month_value, current_date_value);",
"current_time.setHours(0);",
"var lmp_month_value = parseFloat(lmp_month.options[lmp_month.selectedIndex].value);",
"var lmp_date_value = parseFloat(lmp_date.options[lmp_date.selectedIndex].value);",
"var lmp_year_value = parseFloat(lmp_year.options[lmp_year.selectedIndex].value);",
"if ((lmp_month_value == 1) && (lmp_date_value > 28)){",
"if ((lmp_year_value % 4) == 0){ lmp_date_value = 29; lmp_date.selectedIndex = 28; }",
"else{ lmp_date_value = 28; lmp_date.selectedIndex = 27; };",
"}",
"if (((lmp_month_value == 3) || (lmp_month_value == 5) || (lmp_month_value == 8) || (lmp_month_value == 10)) && (lmp_date_value > 30)){ lmp_date_value = 30; lmp_date.selectedIndex = 29; };",
"lmp_time.setFullYear(lmp_year_value);",
"lmp_time.setMonth(lmp_month_value, lmp_date_value);",
"lmp_time.setHours(0);",
"edd_lmp.setTime(lmp_time.getTime() + 24192000000);",
"edd_lmp_result.value = monthname[edd_lmp.getMonth()] + ' ' + edd_lmp.getDate() + ', ' + edd_lmp.getFullYear();",
"lmpmillis = current_time.getTime() - lmp_time.getTime();",
"lmpweeks = fixDP(lmpmillis / 604800000, 1);",
"lmpweeksfloor = Math.floor(lmpweeks);",
"lmpdays =  Math.round(7 * (lmpweeks - lmpweeksfloor));",
"daystxt = ' days';",
"if (lmpdays == 1) daystxt = ' day';",
"if ((lmp_year_value >= 0) && (lmp_month_value >= 0) && (lmp_date_value >= 0)){ current_gest_lmp.value = lmpweeksfloor + ' weeks' + ' ' + lmpdays + daystxt; }",
"else{ current_gest_lmp.value = ''; edd_lmp_result.value = '';}",
"var us_month_value = parseFloat(us_month.options[us_month.selectedIndex].value);",
"var us_date_value = parseFloat(us_date.options[us_date.selectedIndex].value);",
"var us_year_value = parseFloat(us_year.options[us_year.selectedIndex].value);",
"if ((us_month_value == 1) && (us_date_value > 28)){",
"if ((us_year_value % 4) == 0){ us_date_value = 29; us_date.selectedIndex = 28; }",
"else{ us_date_value = 28; us_date.selectedIndex = 27; };",
"}",
"if (((us_month_value == 3) || (us_month_value == 5) || (us_month_value == 8) || (us_month_value == 10)) && (us_date_value > 30)){ us_date_value = 30; us_date.selectedIndex = 29; };",
"us_time.setFullYear(us_year_value);",
"us_time.setMonth(us_month_value, us_date_value);",
"us_time.setHours(0);",
"if (Gestational_Age_by_US && (us_year_value >= 0) && (us_month_value >= 0) && (us_date_value >= 0)){",
"edd_us.setTime(us_time.getTime() - (Gestational_Age_by_US * 604800000 ) + 24192000000);",
"edd_us_result.value = monthname[edd_us.getMonth()] + ' ' + edd_us.getDate() + ', ' + edd_us.getFullYear();",
"usmillis = 24192000000 - (edd_us.getTime() - current_time.getTime());",
"usweeks = fixDP(usmillis / 604800000, 1);",
"usweeksfloor = Math.floor(usweeks);",
"usdays =  Math.round(7 * (usweeks - usweeksfloor));",
"daystxt = ' days';",
"if (usdays == 1) daystxt = ' day';",
"current_gest_us.value = usweeksfloor + ' weeks' + ' ' + usdays + daystxt;",
"}",
"else { edd_us_result.value = ''; current_gest_us.value = '';};",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.EstimatedDateDelivery_form){",
"",
"if (Gestational_Age_by_US_param.value && isNaN(Gestational_Age_by_US_param.value)){ Gestational_Age_by_US_param.value = ''; alertNaN(); }",
"if (Gestational_Age_by_US_param.value && Gestational_Age_by_US < 2) {",
"Gestational_Age_by_US = 0;",
"Gestational_Age_by_US_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Gestational Age by US is 2 weeks.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Gestational_Age_by_US_param.value && Gestational_Age_by_US > 40) {",
"Gestational_Age_by_US_param.value = \"\";",
"clrResults();",
"Gestational_Age_by_US = 0;",
"doCalc = false;",
"alert(\"The maximum value for Gestational Age by US is 40 weeks.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.EstimatedDateDelivery_form){",
"",
"current_gest_lmp.value = '';",
"edd_lmp_result.value = '';",
"current_gest_us.value = '';",
"edd_us_result.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
