var title_f41_53_42843="Evaluation of incomplete precocious puberty";
var content_f41_53_42843=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F72054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F72054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of incomplete precocious puberty",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gonadotropin levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        LH response after GnRH stimulation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sex steroid hormone levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Premature adrenarche",
"       </td>",
"       <td>",
"        Prepubertal LH levels",
"       </td>",
"       <td>",
"        No LH response",
"       </td>",
"       <td>",
"        <p>",
"         DHEAS values elevated for age (at Tanner 2 levels, ie, &gt;50 micrograms/dL)",
"        </p>",
"        <p>",
"         17-OHP and testosterone normal",
"        </p>",
"        <p>",
"         Early pubertal response to ACTH",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         No other signs of pubertal development; normal growth rate",
"        </p>",
"        <p>",
"         Gonads prepubertal",
"        </p>",
"        <p>",
"         Onset usually after six years of age",
"        </p>",
"        <p>",
"         Associated more frequently with brain injury, obesity, or children born SGA",
"        </p>",
"       </td>",
"       <td>",
"        Monitor for possible early progression to full puberty",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Premature thelarche",
"       </td>",
"       <td>",
"        Prepubertal LH levels",
"       </td>",
"       <td>",
"        No LH response, normal FSH response",
"       </td>",
"       <td>",
"        Prepubertal",
"       </td>",
"       <td>",
"        No other signs of pubertal development; normal growth rate",
"       </td>",
"       <td>",
"        Monitor for possible early progression to full puberty",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LH: luteinizing hormone; GnRH: gonadotropin-releasing hormone; DHEAS: dehydroepiandrosterone sulfate; 17-OHP: 17-hydroxyprogesterone; ACTH: adrenocorticotropic hormone; SGA: small for gestational age; FSH: follicle-stimulating hormone.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
