var script_f41_54_42849=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function MyeloDysplasiaRevised_fx() {",
"with(document.MyeloDysplasiaRevised_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + 1;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc1[3].checked){",
"Score = Score + 3;",
"}",
"if (cc1[4].checked){",
"Score = Score + 4;",
"}",
"if (cc2[0].checked){",
"Score = Score + 0;",
"}",
"if (cc2[1].checked){",
"Score = Score + 1;",
"}",
"if (cc2[2].checked){",
"Score = Score + 2;",
"}",
"if (cc2[3].checked){",
"Score = Score + 3;",
"}",
"if (cc3[0].checked){",
"Score = Score + 0;",
"}",
"if (cc3[1].checked){",
"Score = Score + 1;",
"}",
"if (cc3[2].checked){",
"Score = Score + 1.5;",
"}",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 0.5;",
"}",
"if (cc4[2].checked){",
"Score = Score + 1;",
"}",
"if (cc5[0].checked){",
"Score = Score + 0;",
"}",
"if (cc5[1].checked){",
"Score = Score + 0.5;",
"}",
"Score = Math.round(Score * 100) / 100;",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if (Score <=1.5){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >1.5 ) && (Score <=3)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >3 ) && (Score <=4.5)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >4.5 ) && (Score <=6)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"if (Score >6){ document.getElementById('rr1_5').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"document.getElementById('rr1_5').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
