var title_f41_54_42859="Pertussis CDC case definitions";
var content_f41_54_42859=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F80177&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F80177&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Centers for Disease Control and Prevention case definition for pertussis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A clinical case is defined as a person:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Who has a cough illness lasting at least two weeks with one of the following: paroxysms of coughing, inspiratory \"whoop,\" or post-tussive vomiting, and without other apparent cause (as reported by a health professional)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        The laboratory criteria for diagnosis are:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The isolation of B. pertussis from a clinical specimen, or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive polymerase chain (PCR) reaction assay for B. pertussis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A confirmed case is defined as a person:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        With an acute cough illness of any duration who is culture positive from nasopharyngeal secretions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Who meets the clinical case definition (see text) with laboratory confirmation by PCR from nasopharyngeal secretions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Who meets the clinical case definition (see text) and is epidemiologically linked directly to a case confirmed by either culture or PCR from nasopharyngeal secretions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A probable case is defined as a person:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Who meets the clinical case definition (see text) without laboratory confirmation or an epidemiologic link to a laboratory-confirmed case",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Centers for Disease Control and Prevention, Council of State and Territorial Epidemiologists. Pertussis-1997 Case Definition. Available at",
"     <a href=\"file://www.cdc.gov/osels/ph_surveillance/nndss/casedef/pertussis_1997.htm\" target=\"_blank\">",
"      file://www.cdc.gov/osels/ph_surveillance/nndss/casedef/pertussis_1997.htm",
"     </a>",
"     . 1997.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
