" <div id=\"topicContent\">",
"  <div id=\"drugTitle\">",
"   Ranitidine: Drug information",
"  </div>",
"  <div id=\"lexiTitleImg\">",
"   <img height=\"17\" src=\"./../images/lexiComp/Lexicomp_2012_71x17.myextg\" width=\"71\"/>",
"  </div>",
"  <div class=\"clear\">",
"  </div>",
