var title_f41_56_42892="Recom Adj Sys Rx in Older women with br ca";
var content_f41_56_42892=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F81185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F81185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for adjuvant systemic therapy in women older than 70 years of age with breast cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Suggested therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Very low",
"       </td>",
"       <td class=\"sublist1_start\">",
"        If all of the following:",
"       </td>",
"       <td rowspan=\"2\">",
"        No treatment or hormonal therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <p>",
"         Tumor size &lt;1 cm",
"        </p>",
"        <p>",
"         Histologic grade 1",
"        </p>",
"        <p>",
"         ER/PR-positive",
"        </p>",
"        <p>",
"         HER2-negative",
"        </p>",
"        <p>",
"         No angiolymphatic invasion",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Low",
"       </td>",
"       <td class=\"sublist1_start\">",
"        If all of the following:",
"       </td>",
"       <td rowspan=\"2\">",
"        Hormonal therapy alone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <p>",
"         Tumor size &le;2 cm",
"        </p>",
"        <p>",
"         Node-negative",
"        </p>",
"        <p>",
"         Favorable histopathologic features (eg, grade 1)",
"        </p>",
"        <p>",
"         ER/PR-positive",
"        </p>",
"        <p>",
"         HER2-negative",
"        </p>",
"        <p>",
"         No angiolymphatic invasion",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Intermediate",
"       </td>",
"       <td class=\"sublist1_start\">",
"        If node negative and at least one of the following:",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         Hormonal therapy is recommended if ER/PR-positive",
"        </p>",
"        <p>",
"         Addition of chemotherapy suggested if patient healthy and willing to accept risks",
"        </p>",
"        <p>",
"         If HER2-positive, addition of trastuzumab is suggested, if the patient is healthy, lacks cardiac comorbidity, and is willing to accept the risks",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <p>",
"         Tumor size &gt;2 cm",
"        </p>",
"        <p>",
"         Unfavorable histopathologic features (eg, grade 2 to 3)",
"        </p>",
"        <p>",
"         ER/PR-negative",
"        </p>",
"        <p>",
"         HER2-positive",
"        </p>",
"        <p>",
"         Presence of angiolymphatic invasion",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Tumor &ge;2 cm and ER/PR-positive and high risk gene expression analysis (eg, Oncotype DX)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         If one to three involved axillary nodes and ER/PR-positive and HER2-negative",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        High",
"       </td>",
"       <td class=\"sublist1_start\">",
"        If one to three involved axillary nodes and ER/PR-negative, or HER2-positive",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         Chemotherapy recommended",
"        </p>",
"        <p>",
"         If HER2-positive, add trastuzumab",
"        </p>",
"        <p>",
"         If ER/PR-positive, add hormonal therapy",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         Four or more involved axillary nodes",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ER/PR: estrogen and/or progesterone receptor.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f41_56_42892=[""].join("\n");
