var title_f41_59_42941="Antifungal susceptibility testing indications";
var content_f41_59_42941=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F52140&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F52140&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for antifungal susceptibility testing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical setting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"7\">",
"        Routine",
"       </td>",
"       <td>",
"        &bull; Species level identification of all",
"        <em>",
"         Candida",
"        </em>",
"        isolates from deep sites (eg, blood, normally sterile fluids, tissues, abscesses).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Species level identification of",
"        <em>",
"         Aspergillus",
"        </em>",
"        , genus level for all other molds.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Species level (if possible) identification of non-candidal yeast isolates from deep sites (eg, blood, normally sterile fluids, tissues, abscesses).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Selection of therapy is generally based on published consensus guidelines and review of survey data on the organism-drug combination in question.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Routine susceptibility testing of fluconazole, voriconazole,&nbsp;and&nbsp;an echinocandin&nbsp;against",
"        <em>",
"         C. glabrata",
"        </em>",
"        and fluconazole against non-candidal yeast isolates from deep sites (eg, blood, normally sterile fluids, tissues, abscesses).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Susceptibility testing of fluconazole, voriconazole, an echinocandin, and flucytosine against other species of",
"        <em>",
"         Candida",
"        </em>",
"        may be helpful in special circumstances, but not routinely, since susceptibility is usually predictable.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Susceptibility of",
"        <em>",
"         Candida",
"        </em>",
"        spp other than",
"        <em>",
"         C. glabrata",
"        </em>",
"        to echinocandins may be assumed unless initial response is suboptimal. Susceptibility testing of isolates to an echinocandin should be considered if there is a prior history of exposure to this class of agents.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Mucosal&nbsp;candidiasis",
"       </td>",
"       <td>",
"        &bull; Susceptibility testing may be useful for patients unresponsive to therapy.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Invasive disease with clinical failure of initial therapy",
"       </td>",
"       <td class=\"sublist1_start\">",
"        &bull; Consider susceptibility testing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Candida",
"        </em>",
"        spp: amphotericin B, flucytosine, fluconazole, voriconazole, echinocandins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. neoformans",
"        </em>",
"        : fluconazole, amphotericin B, flucytosine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Aspergillus",
"        </em>",
"        species: amphotericin B, posaconazole, itraconazole, voriconazole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Consultation with an experienced microbiologist recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"12\">",
"        Infection with species with high rates of intrinsic or acquired resistance",
"       </td>",
"       <td class=\"sublist1_start\">",
"        &bull; Susceptibility testing not necessary when intrinsic resistance is known.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         A. terreus",
"        </em>",
"        : amphotericin B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. krusei",
"        </em>",
"        : fluconazole, flucytosine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Cryptococcus",
"        </em>",
"        and",
"        <em>",
"         Trichosporon",
"        </em>",
"        : echinocandins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Rhodotorula",
"        </em>",
"        : azoles, echinocandins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Mucorales",
"        </em>",
"        : voriconazole, echinocandins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        &bull; For species with significant rates of acquired resistance, monitor closely for signs of failure and perform susceptibility testing as needed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. glabrata",
"        </em>",
"        : fluconazole, voriconazole, amphotericin B, echinocandins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. krusei",
"        </em>",
"        : amphotericin B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. lusitaniae",
"        </em>",
"        : amphotericin B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         C. rugosa",
"        </em>",
"        : amphotericin B, fluconazole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        -",
"        <em>",
"         Candida",
"        </em>",
"        spp: flucytosine, when used for endocarditis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Unusual organisms",
"       </td>",
"       <td>",
"        &bull; Susceptibility testing may be helpful when treating patients infected with unusual organisms for which susceptibility patterns have not been well established or are unpredictable.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Mold infections",
"       </td>",
"       <td>",
"        &bull; Species level identification of",
"        <em>",
"         Aspergillus",
"        </em>",
"        , genus level for all other molds.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Routine susceptibility testing not recommended.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Susceptibility of",
"        <em>",
"         Aspergillus",
"        </em>",
"        species to itraconazole, voriconazole, and posaconazole may help to determine cross-resistance.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Clinical interpretive criteria have not been established for any agents.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Pfaller MA. Antifungal susceptibility testing methods. Curr Drug Targets 2005; 6:929.",
"       </li>",
"       <li>",
"        Rex JH, Pfaller MA. Has antifungal susceptibility testing come of age? Clin Infect Dis 2002; 35:982.",
"       </li>",
"       <li>",
"        Pappas PG, Kauffman CA, Andes D, et al. Clinical practice guidelines for the management of candidiasis: 2009 update by the Infectious Diseases Society of America. Clin Infect Dis 2009; 48:503.",
"       </li>",
"       <li>",
"        Pfaller MA, Wu WL. Antifungal susceptibility testing: new technology and clinical applications. Infect Dis Clin N Am 2001; 15:1227.",
"       </li>",
"       <li>",
"        Spellberg BJ, Filler SG, Edwards JE Jr. Current treatment strategies for disseminated candidiasis. Clin Infect Dis 2006; 42:244.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
