var title_f41_8_42124="Factors causing nocturia";
var content_f41_8_42124=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F67664&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F67664&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Factors potentially causative of nocturia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        Small volume bladder voids",
"       </td>",
"       <td>",
"        Urgency, sometimes with urge incontinence, urinary frequency (&gt;8 voids/24 hours), and/or nocturia.",
"       </td>",
"       <td>",
"        Daytime voids &ge; 8, urgency, nocturia, and urge incontinence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Benign prostatic hyperplasia (BPH) or bladder outlet obstruction",
"       </td>",
"       <td>",
"        BPH: AUA-7 SI &gt;8 and peak uroflow between 4 and 15 ml/sec; BOO: evidence of obstruction, including peak uroflow less than 15 ml/sec",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinary tract infection",
"       </td>",
"       <td>",
"        Leukocyte esterase on U/A; presence of white blood cells &gt;5 HPF on microscopic examination; &gt;1000 colony forming units by culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low bladder capacity",
"       </td>",
"       <td>",
"        Cystometric capacity less than 150 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"6\">",
"        Increased urine output at night",
"       </td>",
"       <td>",
"        Nocturnal polyuria",
"       </td>",
"       <td>",
"        35 percent or more of 24-hour urine output occurring during sleep hours, may be related to loss of diurnal variation or deficiency for arginine vasopressin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral edema (without heart failure)",
"       </td>",
"       <td>",
"        Presence of pitting edema 10 cm above ankle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congestive heart failure (HF)",
"       </td>",
"       <td>",
"        echocardiographic evidence of a LVEF &lt;35 percent; presence of S3; bilateral lung crackles; Use of an ACEI for HF",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor control of diabetes mellitus",
"       </td>",
"       <td>",
"        Random glucose &gt;200 mg/dl (11.1 mmol/l); glucosuria on urine dipstick",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Excessive fluid intake throughout day or large fluid intake immediately prior to bedtime",
"       </td>",
"       <td>",
"        Analysis of self-reported intake or recorded voiding diary or fluid intake record",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intake of diuretic substances",
"       </td>",
"       <td>",
"        Analysis of self-reported intake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Sleep related disorders",
"       </td>",
"       <td>",
"        Difficulty with sleep maintenance",
"       </td>",
"       <td>",
"        Self-reported sleep latency of &gt;30 minutes following first awakening for nocturia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sleep apnea",
"       </td>",
"       <td>",
"        Daytime sleepiness, loud snoring, witnessed breathing interruptions, or awakenings due to gasping or choking in the presence of at least 5 obstructive respiratory events per hour of sleep*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Restless leg syndrome or periodic limb movements",
"       </td>",
"       <td>",
"        History, partner report, or nighttime sleep study",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unknown mechanism",
"       </td>",
"       <td>",
"        Hypertension",
"       </td>",
"       <td>",
"        &gt;140/90 mm Hg supine",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AUA SI: American Urological Association Symptom Index; U/A: urinalysis; HPF: high powered field; ACE-I: angiotensin-converting enzyme inhibitors; LVEF: left-ventricular ejection fraction.",
"     <br>",
"      * Epstein, LJ, Kristo, D, Strollo, PJ, et al. Clinical guidelines for the evaluation, management, and long-term treatment of obstructive sleep apnea in adults. J Clin Sleep Med 2009; 5:263.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
