" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Amnioinfusion: Technique",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/12/43203/contributors\">",
