" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology, clinical manifestations and diagnosis of norovirus and related viruses",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     John J Treanor, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     Martin S Hirsch, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/14/43241/contributors\">",
"     Elinor L Baron, MD, DTMH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
