var title_f42_14_43245="Causes of failure to thrive";
var content_f42_14_43245=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56603&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56603&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of failure to thrive",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inadequate calorie intake",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Weight gain during refeeding",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate feeding technique",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Disturbed mother/child relationship*",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate nutrient intake- eg, excess fruit juice consumption",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Factitious food allergy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inadequate quantity of food",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Inappropriate food for age",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Neglect",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            No weight gain during refeeding",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Psychosocial problems*",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Maternal/infant dysfunction",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Economic deprivation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Mechanical problems- adenoidal hypertrophy, dental lesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Insufficient lactation in mother",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cleft palate",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Nasal obstruction",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Sucking or swallowing dysfunction (CNS, neuromuscular, esophageal motility problems)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Regurgitation- gastroesophageal reflux",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Malformation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Congenital syndromes- alcohol, pheytoin, drugs",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Genetic syndromes- Turner",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Inadequate appetite or inability to eat large amounts",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Psychosocial problems- apathy*",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Cardiopulmonary disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hypotonia- muscle weakness",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Anorexia of chronic infection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Immune deficiency diseases- HIV infection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Endocrine disorders- hypothyroidism, diabetes insipidus",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            CNS tumors",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Genetic syndromes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Metabolic conditions- lead toxicity, iron or zinc deficiency",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Anemia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic constipation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Disturbance in appetite and satiety",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Inadequate calorie absorption",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            No weight gain during refeeding; increased losses",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Psychosocial problems (refeeding diarrhea, intercurrent illnesses, rumination, regurgitation)*",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Malabsorption- diarrhea (lactose intolerance, cystic fibrosis, cardiac disease, malrotation, inflammatory bowel disease, milk allergy, parasites, celiac disease)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Vomiting or \"spitting up\" or diarrhea (gastroenteritis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Intestinal tract obstruction (pyloric stenosis, hernia, malrotation, intussusception, chalasia)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Biliary atresia/cirrhosis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            CNS problems- increased intracranial pressure (subdural hematoma)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Chronic metabolic problems (hypercalcemia, storage diseases, and inborn errors of metabolism such as galactosemia, methylmalonic acidemia, renal tubular acidosis, diabetes mellitus, adrenal insufficiency)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Increased calorie requirements",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Hyperthyroidism",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Cerebral palsy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Malignancy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Chronic systemic disease (juvenile idiopathic arthritis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Chronic systemic infection (UTI, tuberculosis, toxoplasmosis)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Chronic respiratory insufficiency (bronchopulmonary dysplasia)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Congenital or acquired heart disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Anemia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Toxins (lead)",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Environmental causes are the most common source of problems.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
