var title_f42_18_43307="DSM-IV-TR dx criteria for antisocial personality disorder";
var content_f42_18_43307=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F80887&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F80887&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR diagnostic criteria for antisocial personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        A. There is a pervasive pattern of disregard for and violation of the rights of others indicated by three or more of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        1. Failure to conform to social norms with respect to lawful behaviors as indicated by repeatedly performing acts that are grounds for arrest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        2. Deceitfulness, as indicated by repeated lying, use of aliases, or conning others for personal profit or pleasure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        3. Impulsivity or failure to plan ahead",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        4. Irritability and aggressiveness, as indicated by repeated physical fights or assaults",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        5. Reckless disregard for safety of self or others",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        6. Consistent irresponsibility, as indicated by repeated failure to sustain consistent work behavior or honor financial obligations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        7. Lack of remorse, as indicated by being indifferent to, or rationalizing having hurt, mistreated, or stolen from another",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        B. The individual is at least 18 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        C. There is evidence of conduct disorder with onset before age 15",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        D. The occurrence of antisocial behavior is not exclusively during the course of schizophrenia or a manic episode",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
