" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Gynecologic examination of the newborn and child",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
