var content_f42_19_43323=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnoses established \"definitively\" by pleural fluid analysis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disease",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic pleural fluid tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Empyema",
"       </td>",
"       <td>",
"        Observation (pus, putrid odor); culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malignancy",
"       </td>",
"       <td>",
"        Positive cytology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lupus pleuritis",
"       </td>",
"       <td>",
"        LE cells present; pleural fluid serum ANA &gt;1.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tuberculous pleurisy",
"       </td>",
"       <td>",
"        Positive AFB stain, culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Esophageal rupture",
"       </td>",
"       <td>",
"        High salivary amylase, pleural fluid acidosis (often as low as 6.00)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fungal pleurisy",
"       </td>",
"       <td>",
"        Positive KOH stain, culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chylothorax",
"       </td>",
"       <td>",
"        Triglycerides (&gt;110 mg/dL); lipoprotein electrophoresis (chylomicrons)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemothorax",
"       </td>",
"       <td>",
"        Hematocrit (pleural fluid/blood &gt;0.5)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinothorax",
"       </td>",
"       <td>",
"        Creatinine (pleural fluid/serum &gt;1.0)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peritoneal dialysis",
"       </td>",
"       <td>",
"        Protein (&lt;1 g/dL); glucose (300 to 400 mg/dL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extravascular migration of central venous catheter",
"       </td>",
"       <td>",
"        Observation (milky if lipids are infused); pleural fluid/serum glucose &gt;1.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rheumatoid pleurisy",
"       </td>",
"       <td>",
"        Characteristic cytology",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
