var title_f42_19_43324="Serotonin syndrome drugs";
var content_f42_19_43324=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F64604&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F64604&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Examples of drugs that can precipitate serotonin syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Drugs involved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increases serotonin formation",
"       </td>",
"       <td>",
"        Tryptophan",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Increases release of serotonin",
"       </td>",
"       <td>",
"        Amphetamines (including: dextroamphetamine, methamphetamine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cocaine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MDMA (Ecstasy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amphetamine derivatives (including: fenfluramine, dexfenfluramine, phentermine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levodopa, Carbidopa-levodopa (indirectly causes release serotonin)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"18\">",
"        Impairs reuptake from the synaptic cleft into the presynaptic neuron",
"       </td>",
"       <td>",
"        Cocaine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MDMA (Ecstasy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meperidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tramadol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pentazocine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Selective serotonin reuptake inhibitors (SSRIs) (including: citalopram, escitalopram, fluoxetine, fluvoxamine, paroxetine, and sertraline)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serotonin-norepinephrine reuptake inhibitors (SNRIs) (including: desvenlafaxine, duloxetine, milnacipran, and venlafaxine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dopamine-norepinephrine reuptake inhibitors (including: bupropion)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serotonin modulators: (including: nefazodone, trazodone, and vilazodone)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tricyclic antidepressants (TCAs) (including: amitriptyline, amoxapine, clomipramine, desipramine, doxepin, imipramine, maprotiline, nortriptyline, protriptyline, trimipramine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        St. John's wort (Hypericum perforatum)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5-HT3 receptor antagonists (including: ondansetron, granisetron)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metoclopramide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Valproate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carbamazepine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sibutramine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dextromethorphan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyclobenzaprine",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Inhibits serotonin metabolism (ie, inhibits monoamine oxidase activity)",
"       </td>",
"       <td>",
"        Monoamine oxidase inhibitors (MAOIs) (including: phenelzine, tranylcypromine, isocarboxazid, moclobemide, selegiline, rasagiline, linezolid, methylene blue, procarbazine, Syrian rue [Peganum harmala, harmine])",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Direct serotonin agonist",
"       </td>",
"       <td>",
"        Buspirone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Triptans (including: sumatriptan, rizatriptan, others)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ergot derivatives (including: ergotamine, methylergonovine)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fentanyl",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lysergic acid diethylamide (LSD)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Increases sensitivity of postsynaptic receptor",
"       </td>",
"       <td>",
"        Lithium",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data courtesy of authors with additional data from: Boyer EW and Shannon M; The serotonin syndrome; NEJM 2005;352:1112.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
