var title_f42_19_43325="World Health Organization surgical safety checklist";
var content_f42_19_43325=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F52392&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F52392&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    World Health Organization surgical safety checklist",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1\" colspan=\"2\">",
"            Sign in",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2\" colspan=\"2\">",
"            Before induction of anesthesia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Patient has confirmed:",
"            <ul>",
"             <li>",
"              Identity",
"             </li>",
"             <li>",
"              Site",
"             </li>",
"             <li>",
"              Procedure",
"             </li>",
"             <li>",
"              Consent",
"             </li>",
"            </ul>",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Site marked/not applicable",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Anesthesia safety check completed",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Pulse oximeter on patient and functioning",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td colspan=\"2\">",
"            <strong>",
"             Does patient have a:",
"            </strong>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\">",
"            Known allergy?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            No",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Yes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\">",
"            Difficult airway/aspiration risk?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            No",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Yes, and equipment/assistance available",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\">",
"            Risk of &gt;500 mL blood loss (7 mL/kg in children)?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            No",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Yes, and adequate intravenous access and fluids planned",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1\" colspan=\"2\">",
"            Time out",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2\" colspan=\"2\">",
"            Before skin incision",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Confirm all team members have introduced themselves by name and role",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Surgeon, anaesthesia professional and nurse verbally confirm",
"            <ul>",
"             <li>",
"              Patient",
"             </li>",
"             <li>",
"              Site",
"             </li>",
"             <li>",
"              Procedure",
"             </li>",
"            </ul>",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td colspan=\"2\">",
"            <strong>",
"             Anticipated critical events",
"            </strong>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Surgeon reviews: What are the critical or unexpected steps, operative duration, anticipated blood loss?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Anesthesia team reviews: Are there any patient-specific concerns?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Nursing team reviews: Has sterility (including indicator results) been confirmed? Are there equipment issues or any concerns?",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td colspan=\"2\">",
"            Has antibiotic prophylaxis been given within the last 60 minutes?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Yes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Not applicable",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\">",
"            Is essential imaging displayed?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Yes",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Not applicable",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1\" colspan=\"2\">",
"            Sign out",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2\" colspan=\"2\">",
"            Before patient leaves operating room",
"           </td>",
"          </tr>",
"          <tr>",
"           <td colspan=\"2\">",
"            <strong>",
"             Nurse verbally confirms with the team:",
"            </strong>",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            The name of the procedure recorded",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            That instrument, sponge and needle counts are correct (or not applicable)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            How the specimen is labeled (including patient name)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Whether there are any equipment problems to be addressed",
"           </td>",
"          </tr>",
"          <tr class=\"divider_top\">",
"           <td>",
"            __",
"           </td>",
"           <td>",
"            Surgeon, anesthesia professional and nurse review the key concerns for recovery and management of this patient",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This checklist is not intended to be comprehensive. Additions and modifications to fit local practice are encouraged.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Weiser T, Haynes A, Dziekan G, et al. Effect of a 19-item surgical safety checklist during urgent operations in a global patient population. Ann Surg 2010; 251:976. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
