" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H7708202\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY &amp; RECOMMENDATIONS",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
