var title_f42_26_43436="Potential Rx in scleroderma";
var content_f42_26_43436=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F57635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F57635&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    New potential therapies for systemic sclerosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Agent/approach",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Immune",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rapamycin",
"       </td>",
"       <td>",
"        Blocks cytokine action responsible for T cell activation/proliferation",
"       </td>",
"       <td>",
"        Possible benefit in a small trial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycophenolate mofetil",
"       </td>",
"       <td>",
"        Inhibits lymphocyte purine biosynthesis and so DNA production-inhibits lymphocyte proliferation",
"       </td>",
"       <td>",
"        Benefit suggested in observational studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Oral tolerance",
"       </td>",
"       <td>",
"        Induces regulatory suppression of specific immune response through oral administration of small amounts of antigen",
"       </td>",
"       <td>",
"        Proposed and ongoing studies include collagen (I) and topoisomerase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Vascular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Antioxidants",
"       </td>",
"       <td>",
"        Reduces free radical mediated endothelial damage and lipoprotein oxidation",
"       </td>",
"       <td>",
"        Benefit in open studies for nutrient antioxidants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Superoxide dismutase",
"       </td>",
"       <td>",
"        Reduces free radical damage",
"       </td>",
"       <td>",
"        No evaluation yet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Oral prostacyclin",
"       </td>",
"       <td>",
"        Vasodilation, also may modulate cytokine release",
"       </td>",
"       <td>",
"        Helps Raynaud in preliminary controlled trials but effect on digital ulceration unproven",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Endothelin antagonists",
"       </td>",
"       <td>",
"        Block ET-induced vasospasm",
"       </td>",
"       <td>",
"        Beneficial for pulmonary hypertension and may reduce the incidence of digital ulcers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Losartan",
"       </td>",
"       <td>",
"        Angiotensin antagonist",
"       </td>",
"       <td>",
"        Benefit suggested for secondary Raynaud phenomenon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Antifibrotic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anticytokine antibodies",
"       </td>",
"       <td>",
"        Blocks mediators of fibroblast activation (eg, TGFb, IL-6)",
"       </td>",
"       <td>",
"        Toxicity likely to be problematic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enzyme antagonists",
"       </td>",
"       <td>",
"        Lysal and prolyl hydroxylase inhibitors to block post-translational modification of collagen",
"       </td>",
"       <td>",
"        No data yet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pretranslational inhibition",
"       </td>",
"       <td>",
"        Block collagen gene transcription or mRNA translation using antisense nucleotides or gene therapy",
"       </td>",
"       <td>",
"        Hypothetical and dependent upon progress in understanding of collagen gene regulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Selective tyrosine kinase inhibitors",
"       </td>",
"       <td>",
"        Block myofibroblast differentiation and vascular proliferation",
"       </td>",
"       <td>",
"        Small studies of imatinib have suggested limited benefit but failed to show overall superiority over conventional therapy in diffuse SSc. More trials are needed.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
