var title_f42_27_43452="Clonidine and related imidazoline poisoning - Rapid overview";
var content_f42_27_43452=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63110&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63110&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clonidine and related imidazoline poisoning: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms usually appear within 1 hour of oral ingestion of pills or liquids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Delayed symptoms may be seen after clonidine transdermal patch exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May mimic opioid intoxication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Common findings: lethargy, coma, bradycardia, hypotension, respiratory depression, apnea, small pupils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Initial evaluation: fingerstick blood glucose (if any alteration in mental status), 12-lead ECG, serum salicylate and acetaminophen levels (if suicidal attempt), pregnancy test (in women of childbearing age)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptomatic patients: arterial or venous pH and pCO",
"        <sub>",
"         2",
"        </sub>",
"        , serum electrolytes, BUN, creatinine, and chest radiograph if serious and persistent respiratory depression or coma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Remove any transdermal clonidine patches",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Support airway and breathing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Respiratory depression or mental status depression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Naloxone, 0.1 mg/kg (maximum single dose 2 mg; may repeat up to 10 mg total dose)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Symptomatic bradycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1st line: atropine, 0.02 mg/kg IV (minimum dose: 0.1 mg, maximum single dose: 1 mg), repeat every 5 minutes to maximum total dose: 0.04 mg/kg or 3 mg in adults",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2nd line: dopamine (initiate at 5-10 mcg/kg per minute to start, titrate to response; maximum dose 20 mcg/kg per minute)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Hypotension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        1st line: crystalloid (intravenous normal saline or Ringer's lactate 20 mL/kg (maximum 1 L), up to 60 to 100 mL/kg (3 to 5 L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        2nd line: dopamine (initiate at 5-10 mcg/kg per minute to start, titrate to response; maximum dose 20 mcg/kg per minute)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not treat asymptomatic hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer activated charcoal (AC), 1 gm/kg (maximum dose, 50 grams) to alert patients who present within 1 to 2 hours of ingestion unless contraindicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer AC 1 gm/kg (maximum dose, 50 grams) followed by whole bowel irrigation at 500 mL to 1 L per hour in alert patients who have ingested a clonidine transdermal patch unless contraindicated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
