var title_f42_32_43531="Tension-type headache criteria";
var content_f42_32_43531=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F79672&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F79672&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tension-type headache diagnostic criteria",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        A. At least 10 episodes fulfilling criteria B through E. The number of days per month with such headache determines the subtype:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &lt;1 day a month: infrequent episodic TTH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1 to 14 days a month: frequent episodic TTH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &ge;15 days a month: chronic TTH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        B. Headache lasting from 30 minutes to 7 days for episodic TTH; headache lasts hours or may be continuous for chronic TTH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        C. At least two of the following pain characteristics:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pressing/tightening (nonpulsating) quality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mild or moderate intensity (may inhibit but does not prohibit activities)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bilateral location",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No aggravation by walking stairs or similar routine physical activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        D. Both of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No nausea or vomiting (anorexia may occur)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Photophobia and phonophobia are absent, or one but not the other may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        E. Not attributed to another disorder",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     TTH: tension-type headache.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Headache classification subcommittee of the International Headache Society. The International Classification of Headache Disorders: 2nd edition. Cephalalgia 2004; 24(Suppl 1):9.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
