var title_f42_38_43627="Dissociative PTSD vs hyperaroused PTSD";
var content_f42_38_43627=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F59632&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F59632&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Typical differences between dissociative PTSD and hyperaroused PTSD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dissociative subtype of PTSD",
"       </td>",
"       <td class=\"subtitle1\">",
"        Hyperaroused PTSD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Etiology",
"       </td>",
"       <td>",
"        Likely to be more severe, chronic, repeated, usually childhood and adult trauma",
"       </td>",
"       <td>",
"        Likely to be later-occurring trauma and/or less cumulative trauma",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"6\">",
"        Likely response when presented with traumatic narrative or triggers",
"       </td>",
"       <td>",
"        Dissociation, numbing",
"       </td>",
"       <td>",
"        Terror",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased, blunted autonomic arousal",
"       </td>",
"       <td>",
"        Increased autonomic arousal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased heart rate",
"       </td>",
"       <td>",
"        Increased heart rate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cortisol release is delayed",
"       </td>",
"       <td>",
"        Rapidly increased cortisol level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased skin conductance",
"       </td>",
"       <td>",
"        Increased skin conductance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brain areas activated that may over-control emotion and alter sense of self (eg, MPFC)",
"       </td>",
"       <td>",
"        Brain areas activated that may under-control emotion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PTSD: posttraumatic stress disorder; MPFC: medial prefrontal cortex.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Vermetten E, Dorahy MJ, Spiegel D. Traumatic Dissociation Neurobiology and Treatment, American Psychiatric Press, Washington DC, 2007.",
"       </li>",
"       <li>",
"        Brand B, Loewenstein RJ. Dissociative disorders: An overview of assessment, phenomenology and treatment, Psychiatric Times, 2010.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
