var title_f42_4_43083="Revised cardiac risk index";
var content_f42_4_43083=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F57075&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F57075&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Revised Goldman cardiac risk index (RCRI)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Six independent predictors of major cardiac complications",
"        <sup>",
"         [1]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High-risk type of surgery (examples include vascular surgery and any open intraperitoneal or intrathoracic procedures)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of ischemic heart disease (history of MI or a positive exercise test, current complaint of chest pain considered to be secondary to myocardial ischemia, use of nitrate therapy, or ECG with pathological Q waves; do not count prior coronary revascularization procedure unless one of the other criteria for ischemic heart disease is present)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of HF",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of cerebrovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diabetes mellitus requiring treatment with insulin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Preoperative serum creatinine &gt;2.0 mg/dL (177 &micro;mol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Rate of cardiac death, nonfatal myocardial infarction, and nonfatal cardiac arrest according to the number of predictors",
"        <sup>",
"         [2]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No risk factors - 0.4 percent (95% CI: 0.1-0.8)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        One risk factor - 1.0 percent (95% CI: 0.5-1.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Two risk factors - 2.4 percent (95% CI: 1.3-3.5)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Three or more risk factors - 5.4 percent (95% CI: 2.8-7.9)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        From: Lee TH, Marcantonio ER, Mangione CM, et al. Circulation 1999; 100:1043.",
"       </li>",
"       <li>",
"        From: Devereaux PJ, Goldman L, Cook DJ, et al. CMAJ 2005; 173:627.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
