" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Drug-eluting intracoronary stents: General principles",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     J Dawn Abbott, MD, FACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Donald Cutlip, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Stephan Windecker, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?42/43/43702/contributors\">",
"     Gordon M Saperia, MD, FACC",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
