"       <td rowspan=\"4\">",
"        &uarr;",
"       </td>",
"       <td rowspan=\"4\">",
"        &darr;",
"       </td>",
"       <td>",
"        Hepatomegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neonatal onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe fasting hypoglycemia",
"       </td>",
"      </tr>",
