var title_f42_51_43834="Initial trauma management in the severely injured child";
var content_f42_51_43834=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F64241&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F64241&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 626px\">",
"   <div class=\"ttl\">",
"    Initial trauma management in children with severe multiple trauma",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 606px; height: 1120px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     pCO2: partial pressure of carbon dioxide; O2: oxygen; GCS: Glasgow coma scale; FAST: focused abdominal sonography for trauma.",
"     <br>",
"      * Clinicians should always perform actions in",
"      <span style=\"color:#ff0000\">",
"       RED",
"      </span>",
"      .",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Administer 20 mL/kg of warmed normal saline or Ringer's lactate over 10 to 20 minutes.",
"       <br>",
"        &Delta; Signs of herniation include coma, unilateral pupillary dilation with outward eye deviation followed by hemiplegia, hyperventilation, Cheyne-Stokes respirations, and/or decerebrate or decorticate posturing.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
