var title_f42_57_43932="ECOG performance scale";
var content_f42_57_43932=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F72901%7EPC%2F58785&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F72901%7EPC%2F58785&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Eastern Cooperative Oncology Group (ECOG, Zubrod, WHO) performance scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Performance status",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        0",
"       </td>",
"       <td>",
"        Fully active; no performance restrictions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1",
"       </td>",
"       <td>",
"        Strenuous physical activity restricted; fully ambulatory and able to carry out light work",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2",
"       </td>",
"       <td>",
"        Capable of all selfcare but unable to carry out any work activities. Up and about &gt;50 percent of waking hours.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3",
"       </td>",
"       <td>",
"        Capable of only limited selfcare; confined to bed or chair &gt;50 percent of waking hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4",
"       </td>",
"       <td>",
"        Completely disabled; cannot carry out any selfcare; totally confined to bed or chair",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Excerpted from: Oken MM, et al. Am J Clin Oncol 1982; 5:649.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Karnofsky performance status scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Value",
"       </td>",
"       <td class=\"subtitle1\">",
"        Level of functional capacity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        100",
"       </td>",
"       <td>",
"        Normal, no complaints, no evidence of disease",
"       </td>",
"       <td rowspan=\"3\">",
"        Able to carry on normal activity and to work; no special care needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        90",
"       </td>",
"       <td>",
"        Able to carry on normal activity, minor signs or symptoms of disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        80",
"       </td>",
"       <td>",
"        Normal activity with effort, some signs or symptoms of disease",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        70",
"       </td>",
"       <td>",
"        Cares for self, unable to carry on normal activity or to do active work",
"       </td>",
"       <td rowspan=\"3\">",
"        Unable to work; able to live at home and care for most personal needs; various degrees of assistance needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        60",
"       </td>",
"       <td>",
"        Requires occasional assistance, but is able to care for most needs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        50",
"       </td>",
"       <td>",
"        Requires considerable assistance and frequent medical care",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        40",
"       </td>",
"       <td>",
"        Disabled, requires special care and assistance",
"       </td>",
"       <td rowspan=\"5\">",
"        Unable to care for self; requires equivalent of institutional or hospital care; disease may be progressing rapidly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        30",
"       </td>",
"       <td>",
"        Severely disabled, hospitalization is indicated although death is not imminent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        20",
"       </td>",
"       <td>",
"        Hospitalization is necessary, very sick, active supportive treatment necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10",
"       </td>",
"       <td>",
"        Moribund, fatal processes progressing rapidly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        Dead",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
