var title_f43_2_44075="Romhilt Estes point score";
var content_f43_2_44075=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F51012&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F51012&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Romhilt-Estes point score system for ECG diagnosis of LVH",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criterion",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Points",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Any limb R ware or S wave &ge;2.0 mV (20 mm)",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         OR",
"        </strong>",
"        S in V1 or S in V2 &ge; 3.0 mV (30 mm)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         OR",
"        </strong>",
"        &nbsp;R in V5 or R in V6 &ge;3.0 mV (30 mm)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\" rowspan=\"1\">",
"        ST-T wave changes typical of LVH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Taking digitalis",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Not taking digitalis",
"       </td>",
"       <td class=\"sublist_other\">",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\" rowspan=\"1\">",
"        Left atrial abnormality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        P terminal force in V1 is 1 mm or more in depth with a duration&nbsp;40 ms (0.04 sec)",
"       </td>",
"       <td class=\"sublist_other\">",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Left axis deviation &ge;-30&ordm;",
"       </td>",
"       <td>",
"        2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        QRS duration &ge;90 ms",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intrinsicoid deflection in V5 or V6 &ge;50 ms (0.05 sec)*",
"       </td>",
"       <td>",
"        1",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     A score of 5 or more indicates \"definite\" LVH; a score of 4 indicates \"probable\" LVH.",
"     <br>",
"      * Intrinsicoid deflection is defined as interval between beginning of QRS interval and the peak of the R wave.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
