var title_f43_21_44379="Medication doses for pediatric acute asthma";
var content_f43_21_44379=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53629&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53629&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommended doses of medications to treat children with acute asthma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Albuterol by nebulizer",
"       </td>",
"       <td>",
"        0.15 mg/kg per dose (maximum 5 mg) every 20 minutes for 3 doses then every 1 to 4 hours as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Continuous albuterol by nebulizer",
"       </td>",
"       <td>",
"        0.5 mg/kg per hour",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Albuterol by MDI with spacer",
"       </td>",
"       <td>",
"        Dose is not well-established. Reasonable starting points: 1/4 to 1/3 puff/kg, maximum&nbsp;8 puffs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levalbuterol",
"       </td>",
"       <td>",
"        One-half the recommended dose for racemic albuterol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ipratropium bromide nebulizer solution",
"       </td>",
"       <td>",
"        <p>",
"         &lt;20 kg: 250 mcg/dose",
"        </p>",
"        <p>",
"         &gt;20 kg: 500 mcg/dose",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prednisone",
"       </td>",
"       <td>",
"        2 mg/kg (maximum 60 mg) by mouth for the first dose, and then 1 mg/kg twice daily for subsequent doses starting the following day; a three to ten day course is generally given",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dexamethasone",
"       </td>",
"       <td>",
"        0.6 mg/kg (maximum 16 mg) by mouth or IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV methylprednisolone",
"       </td>",
"       <td>",
"        1 mg/kg (maximum 60 mg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Epinephrine (1:1000)",
"       </td>",
"       <td>",
"        <p>",
"         0.01 mg/kg subcutaneous or intramuscular (maximum 0.4 mg)",
"        </p>",
"        <p>",
"         May be repeated every 10 to 20 minutes for 3 doses",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Terbutaline (1 mg/ml)",
"       </td>",
"       <td>",
"        0.01 mg/kg every 20 minutes for 3 doses, then every 2 to 6 hours as needed, subcutaneous or intramuscular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IV magnesium sulfate",
"       </td>",
"       <td>",
"        75 mg/kg over 20 minutes (maximum 2.5 gm)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Richard Scarfone, MD, FAAP.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
